document.addEventListener("DOMContentLoaded",function(){loadComponent("header-placeholder","components/header.html"),loadComponent("footer-placeholder","components/footer.html")});function loadComponent(e,t){const n=document.getElementById(e);if(!n)return;const o=getPathDepth();fetch(o+t).then(e=>e.text()).then(t=>{t=t.replace(/\[ROOT_URL\]/g,o),n.innerHTML=t,"header-placeholder"===e&&(initMobileNav(),highlightCurrentPage())}).catch(e=>{console.error("Error loading component:",e)})}function getPathDepth(){const e=window.location.pathname.split("/").filter(Boolean);return 0===e.length||1===e.length&&e[0].endsWith(".html")?"":"../".repeat(e.length-(e[e.length-1].endsWith(".html")?1:0))}